<?php session_start();function user(){	if(isset($_SESSION['email']))		echo '<a href="index.php?r=profile">' . $_SESSION["name"] . "</a>";	else		echo '<a href="index.php?r=sign">Sign in</a>';}if(isset($_GET['r']))	$control = $_GET['r'].'/';else	$control = '';	function nameof($dir){	switch($dir)	{		case 'sign':		return 'ورود/خروج';		case 'chatrang':	return 'Chatrang';		case 'dodeca':		return 'Dodeca';		case 'games':		return 'Games';		case 'press':		return 'Press';		case 'verblab':		return 'Persian VerbLab';		case 'verblab1':	return 'Phase I: Creat Verb';		case 'latex':		return 'Persian LaTeX Editor';		case 'profile':		return 'پروفایل';		case 'system':		return 'System';		case 'console':		return 'Console';		case 'me':			return 'About Me';		case 'us':			return 'About Us';		case 'website';		return 'About Website';		case 'transwords':	return 'TransWords';		case 'votes':		return 'Votes';		case 'problems':	return 'Problems';		case 'signout':		return 'خروج از سیستم';		case 'ranks':		return 'رتبه‌بندی';		case 'ideas':		return 'ایده‌ها';		case 'quizzes':		return 'کوئیزها';		case 'election':	return 'گزینش';		case 'report':		return 'گزارش';		default: return $dir;	}}	function print_menu(){	if(isset($_GET['r']))	{		$lcontrol = $_GET['r'].'/';		$dcontrol = $_GET['r'];		echo '<li><a href="index.php"><strong>MathCourse</strong></a></li>';		echo '<li><img src="images/menu-arrow.png"></li>';				$exploded = explode('/',$dcontrol);		$dir = '';		for($i=0; $i<count($exploded); $i++)		{			$dir .= ($dir?'/':'').$exploded[$i];						if($i != count($exploded)-1)			{				echo '<li><a href="index.php?r='.$dir.'"><strong>'.nameof($exploded[$i]).'</strong></a></li>';				echo '<li><img src="images/menu-arrow.png"></li>';			}			else			{				echo '<li class="active"><a href="index.php?r='.$dir.'"><strong>'.nameof($exploded[$i]).'</strong></a></li>';				echo '<li><img src="images/menu-arrow.png"></li>';			}		}	}			else	{		$lcontrol = '';		echo '<li class="active"><a href="index.php"><strong>MathCourse</strong></a></li>';		echo '<li><img src="images/menu-arrow.png"></li>';	}	$dirs = array_filter(glob('protected/'.$lcontrol.'*'), 'is_dir');	foreach($dirs as $dir)	{		$exploded = explode('/',$dir);		$ldir = $exploded[count($exploded)-1];		echo '<li><a href="index.php?r='.$lcontrol.$ldir.'"><strong>'.nameof($ldir).'</strong></a></li>';	}}function title(){	if(isset($_GET['r']))	{		$dcontrol = $_GET['r'];		$exploded = explode('/',$dcontrol);		$return = $exploded[count($exploded)-1];		$return = nameof($return);		$return .= ' - MathCourse';		return $return;	}	else		return 'MathCourse';}	?><!doctype html><html lang="en">	<head>		<script src="scripts/jquery-2.0.2.min.js"></script>		<script src="scripts/jquery-ui.js"></script>		<script src="scripts/bottomnav.js"></script>		<script src="<?php echo 'protected/'.$control.'script.js'; ?>"></script>		<meta charset="utf-8">		<meta name="description" content="Angellandros">		<title><?php echo title(); ?></title>		<link rel="stylesheet" href="css/style.css" type="text/css" />		<LINK href="images/SquareAngellandros.ico" rel="SHORTCUT ICON">		<meta http-equiv="Content-Type" content="text/html;charset=utf-8">				<?php if(file_exists('protected/'.$control.'head.html')) echo file_get_contents('protected/'.$control.'head.html'); ?>	</head>	<body>			<div id="topnav">		<div id="mid">		<div id="topmaintext">			<ul>				<?php print_menu(); ?>			</ul>						<div id="topright">				<?php user(); ?>			</div>		</div>		</div>		</div>		<?php require("protected/bottom.php"); ?>		<?php require('protected/'.$control.'index.php'); ?>		<?php require("protected/bottomer.php"); ?>		</body></html>